import * as React from 'react';
import {rule} from 'nano-theme';

const bg =
  'data:image/gif;base64,';

const className = rule({
  h: '2px',
  bg: `url(${bg})`,
  an: 'bg-running 2s infinite',
  '@keyframes bg-running': {
    '0%': {
      bgp: '0 0',
    },
    '100%': {
      bgp: '102px 0',
    },
  },
});

export const RunningBackground: React.FC<any> = ({children}) => <div className={className}>{children}</div>;
